    private void addActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addActionPerformed
     String name =n.getText();
     String gender ;
     if (g1.isSelected()){
         gender =g1.getText();
         
     }else if (g2.isSelected()){
         gender =g2.getText();
     }else {
         gender = g3.getText();
     }
     java.util.Date dt= d. getDate();
     String country =(String)c.getSelectedItem();
    String address = a.getText();
    String language ="";
    if (l1.isSelected())
        language+=l1.getText()+",";
      if (l2.isSelected())
        language+=l2.getText()+",";
      if (l3.isSelected())
        language+=l3.getText()+",";        
    if (!language.equals("")){
       language=language.substring(0, language.length()-1);
    }
    if( !name.equals("") && dt!=null && !address.equals("")){
    try{   
    java.sql.Date dob=new java.sql.Date(dt.getTime());
    db.DbConnect.s.executeUpdate("Insert into client_info(name,gender,dob,country,address,language) values('"+
            name+"','"+gender+"','"+dob+"','"+country+"','"+address+"','"+language+"')");
    JOptionPane.showMessageDialog(null,"Client Added Successfully ");
    
    }//GEN-LAST:event_addActionPerformed
catch (Exception ex){
    
    
    JOptionPane.showMessageDialog(null,ex);
 
}
    }else{
        JOptionPane.showMessageDialog(null,"please fill the complete form ");
    }
    }
